mapper.sql:
		from
		codegroup as a
		where 1=1

		AND a.cgUse = #{shUseNy}
        AND a.cgdelNy = #{shDelNy}


			AND a.cgRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.cgEdDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.cgSeq = #{shValue}
            AND a.cgName LIKE CONCAT('%',#{shValue},'%')
            AND a.cgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO codegroup ( cgUse, cgName, cgNameEng, cgOrder, cgRegDate, cgdelNy
) values ( #{cgUse}, #{cgName}, #{cgNameEng}, #{cgOrder}, NOW(), 0 )
SELECT last_insert_id()

mapper.update:
UPDATE codegroup SET
	cgUse = #{cgUse}
	,cgName = #{cgName}
	,cgNameEng = #{cgNameEng}
	,cgEdDate = now()
WHERE
	cgSeq = #{cgSeq}
